import React from 'react'
import { language } from '../../utils/utils'

function ShareComment() {
    return (
        <div className='w-100 p-1 border m-auto d-flex gap-2'>
            <textarea style={{maxHeight:50,minHeight:50}} className='form-control' placeholder={language.includes("tr") ? "Ne Düşünüyorsun?" : "What Do You Think?"} />
            <button className='btn btn-primary'>
                {language.includes("tr") ? "Paylaş" : "Share"}
            </button>
        </div>
    )
}

export default ShareComment
