import { user, userInfos } from '../store/auth';
import store from '../store/index'

export const defaultUserImageUrl = "https://firebasestorage.googleapis.com/v0/b/eidos-95e1f.appspot.com/o/Uploads%2FUsers%2Fuserdefault.png?alt=media&token=06397d82-3dc8-4783-b110-a22b5d68e92e";
export const language = navigator.language;
export const localUser = localStorage.getItem('user');

export const setUserData = ({ id }) => {
    store.dispatch(user({
        id: id,
    }));
}

export const setUserInfos = (infos) => {
    console.log(infos)
    console.log("infos")
    store.dispatch(userInfos({
        Email: infos.Email,
        NickName: infos.NickName,
        Name: infos.Name,
        Post: infos.Post,
        ImageUrl: infos.ImageUrl
    }));
}

export const setLocalUser = ({ id }) => {
    localStorage.setItem('user', id)
}
