import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { language } from '../../utils/utils';

function Profile() {

  const { user } = useSelector(state => state.auth);
  const [userInfos, setuserInfos] = useState({})

  useEffect(() => {

    setuserInfos(user)

    return () => {
      setuserInfos({})
    }
  }, [user])


  if (user) {
    return (
      <div className='w-100 h-100 m-auto'>
        <div className='w-75 h-auto m-auto d-flex gap-2 border border-secondary rounded'>
          <img style={{ width: '15%' }} className='w-15 h-100 rounded m-auto' src={userInfos.ImageUrl} />
          <div className='w-100 d-grid'>
            <span className='w-100'>
              <strong>Email: </strong>{userInfos.Email}
            </span>
            <span className='w-100'>
            <strong>{language.includes("tr")?"Kullanıcı Adım":"My Nick Name"}</strong>{userInfos.NickName}
            </span>
            <span className='w-100'>
            <strong>Email: </strong>{userInfos.Name}
            </span>
            <span className='w-100'>
            <strong>Email: </strong>{userInfos.Post}
            </span>
          </div>
        </div>
      </div>
    )
  } else {
    return (
      <></>
    )
  }
}

export default Profile
