import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { mainUrl } from '../../utils/utils';
import axios from 'axios'
import ShareComment from './ShareComment';

function PostDetails() {

    const { allPosts } = useSelector(state => state.allPosts);
    const params = useParams();
    const { postId } = params;
    const [postInfo, setPostInfo] = useState({});
    const [postOwner, setPostOwner] = useState({});

    const getPostInfo = async () => {
        const info = allPosts.filter(post => post._id === postId);
        setPostInfo(info[0]);
    }

    const getUser = async () => {
        axios.get(`${mainUrl}/api/getUser/${postInfo.OwnerId}`)
            .then((result) => {
                if (result.data.error) {
                    console.log(result.data.message)
                } else {
                    setPostOwner(result.data.user);
                }
            })
    }

    useEffect(() => {

        if (postId) {
            getPostInfo();
        }

        return () => {
            setPostInfo({});
        }
    }, [postId]);

    useEffect(() => {

        if (postInfo) {
            getUser();
        }

        return () => {
            setPostOwner({});
        }
    }, [postInfo]);


    if (postId) {
        if (postInfo) {
            if (postOwner) {
                return (
                    <div className='w-75 h-auto m-auto d-grid'>
                        <div className='w-100 d-flex'>
                            <div style={{ width: '10%', height: 'auto' }} className='border border-dark rounded-start p-2 justify-content-center'>
                                <img className='d-flex w-75 m-auto rounded-circle' src={postOwner.ImageUrl} />
                                <strong className='d-flex w-75 m-auto text-center'>
                                    {postOwner.NickName}
                                </strong>
                            </div>

                            <div style={{ width: '90%', height: 'auto' }} className='d-grid gap-2 border border-dark rounded-end p-2'>
                                {postInfo.Files && postInfo.Files.length !== 0 &&
                                    <div id="carouselExampleControls" className="carousel slide w-50 m-auto" data-bs-ride="carousel">
                                        <div className="carousel-inner">
                                            {postInfo.Files.map((file, index) => {
                                                return (
                                                    <div className={`carousel-item ${index === 0 && "active"}`}>
                                                        <img src={file} className="d-block w-100 rounded" alt={file} />
                                                    </div>
                                                )
                                            })}
                                        </div>
                                        {postInfo.Files.length !== 0 &&
                                            <>
                                                <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
                                                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                                                    <span className="visually-hidden">Previous</span>
                                                </button>
                                                <button className="carousel-control-next carousel-caption-color-primary" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
                                                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                                                    <span className="visually-hidden">Next</span>
                                                </button>
                                            </>
                                        }

                                    </div>}
                                <div className='w-100'>
                                    {postInfo.Text}
                                </div>
                            </div>
                        </div>
                        <ShareComment />
                    </div>
                )
            }
        }
    }
}

export default PostDetails
