import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { mainUrl } from '../../utils/utils'
import moment from 'moment'

function PostInfo({ post }) {

    const [postOwner, setPostOwner] = useState({});
    const month = ((new Date).getMonth() + 1);

    const getUser = async () => {
        axios.post(`${mainUrl}/api/getUser`, { userId: post.OwnerId })
            .then((response) => {
                if (response.data.error) {
                    console.log(response.data.message)
                } else {
                    setPostOwner(response.data.user)
                }
            })
    }

    useEffect(() => {

        getUser();

        return () => {

        }
    }, [post.OwnerId])


    return (
        <div className='w-100 p-1 border border-secondary rounded d-flex gap-2 align-items-center'>
            <img className='rounded-circle' src={postOwner.ImageUrl} style={{ width: 36, height: 36 }} />
            <strong className='w-75'>
                {post.Title}
            </strong>
            <div className='w-25'>
                {
                    post.Month !== month ?
                    post.Date
                        :
                        moment(post.FullDate, "YYYYMMDD, h:mm:ss").fromNow()
                }
            </div>
        </div>
    )
}

export default PostInfo
