import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'

function Profile() {

  const { user } = useSelector(state => state.auth);
  const [userInfos, setuserInfos] = useState({})

  useEffect(() => {

    setuserInfos(user)

    return () => {
      setuserInfos({})
    }
  }, [user])


  if (user) {
    return (
      <div className='w-100 h-100 m-auto'>
        <div className='w-75 m-auto d-flex gap-2 border border-secondary rounded'>
          <img style={{ width: '25%' }} className='rounded' src={userInfos.ImageUrl} />
          <div className='d-grid'>
            <span>
              {userInfos.Email}
            </span>
            <span>
              {userInfos.NickName}
            </span>
            <span>
              {userInfos.Name}
            </span>
            <span>
              {userInfos.Post}
            </span>
          </div>
        </div>
      </div>
    )
  } else {
    return (
      <></>
    )
  }
}

export default Profile
