import React, { useState } from 'react'
import { getCurrentUser, language, mainUrl, reportTitleList, subjectList } from '../../utils/utils'
import axios from 'axios'
import { toast } from 'react-hot-toast'
import { useFormik } from 'formik'
import { publishPostValidations, reportValidations } from '../../utils/validations'
import moment from 'moment'
import { useSelector } from 'react-redux'

function Report({ data }) {

    const { user } = useSelector(state => state.auth);
    const [isPublished, setIsPublished] = useState(false);
    const [files, setFiles] = useState([]);

    const { handleChange, handleSubmit, handleReset, values, errors, touched } = useFormik({
        initialValues: {
            title: '',
            detail: ''
        },
        onSubmit: values => {
            console.log(values);
        },
        validationSchema: reportValidations
    });

    return (
        <form className='w-75 m-auto mt-2 mb-2' onSubmit={handleSubmit}>

            <div className='w-100 modal-body d-grid gap-2'>

                <div className='w-100'>
                    <label className='w-100 form-label'>
                        {language.includes("tr") ? "Başlık" : "Title"}
                    </label>
                    <select name='title' className='form-select' defaultValue={values.title} onChange={handleChange}>
                        {reportTitleList.map(report => {
                            return (
                                <option key={report.value} selected={report.value === "1"}>
                                    {language.includes("tr") ? report.tr : report.tr}
                                </option>
                            )
                        })}
                    </select>
                </div>
                {values.title}
                {errors.title && touched.title &&
                    <div className="bg-danger text-light p-2 rounded">
                        {errors.title}
                    </div>}

                <div className='w-100'>
                    <label className='form-label'>
                        {language.includes("tr") ? "Detay" : "Detail"}
                    </label>
                    <div className="form-outline">
                        <textarea name='detail' style={{ minHeight: 200, maxHeight: 200 }} value={values.text} onChange={handleChange} className="form-control" rows="4" placeholder={language.includes("tr") ? "Detay" : "Detail"}></textarea>
                    </div>
                </div>
                {errors.detail && touched.detail &&
                    <div className="bg-danger text-light p-2 rounded">
                        {errors.detail}
                    </div>}

            </div>

            <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" onClick={() => { handleReset(); setFiles([]); }}>
                    {language.includes("tr") ? "Kapat" : "Close"}
                </button>
                {isPublished ?
                    <div className='btn btn-secondary'>
                        {language.includes("tr") ? "Yayınlanıyor..." : "Being Published..."}
                    </div>
                    :
                    <button type='submit' className='btn btn-primary'>
                        {language.includes("tr") ? "Yayınla" : "Publish"}
                    </button>}
            </div>
        </form>
    )
}

export default Report
