import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { language, mainUrl } from '../../utils/utils';
import axios from 'axios'
import ShareComment from './ShareComment';
import Comments from '../Comment/Comments';

function PostDetails() {

    const { allPosts } = useSelector(state => state.allPosts);
    const { user } = useSelector(state => state.auth);
    const params = useParams();
    const { postId } = params;
    const [postInfo, setPostInfo] = useState({});
    const [postOwner, setPostOwner] = useState({});
    const [isShared, setIsShared] = useState(false);
    const [isDeletedComment, setIsDeletedComment] = useState(false);
    const [comments, setComments] = useState([]);

    const getPostInfo = async () => {
        const info = await allPosts.filter(post => post._id === postId);
        setPostInfo(info[0]);
    }

    const getUser = async () => {
        axios.get(`${mainUrl}/api/getUser/${postInfo.OwnerId}`)
            .then((result) => {
                if (result.data.error) {
                    console.log(result.data.message)
                } else {
                    setPostOwner(result.data.user);
                }
            })
    }

    const getComments = async () => {
        axios.get(`${mainUrl}/api/getComments/${postId}`).then(result => {
            if (result.data.error) {
                console.log(result.data.message)
            } else {
                setComments(result.data.comments);
            }
        })
    }

    useEffect(() => {

        if (postId) {
            getPostInfo();
            getComments();
        }

        return () => {
            setPostInfo({});
            setComments([]);
        }

    }, []);

    useEffect(() => {

        if (postId) {
            getPostInfo();
            getComments();
        }

        return () => {
            setPostInfo({});
            setComments([]);
        }
    }, [postId]);

    useEffect(() => {

        if (postInfo) {
            getUser();
        }

        return () => {
            setPostOwner({});
        }

    }, [postInfo]);

    useEffect(() => {

        if (isShared) {
            getComments();
        }

    }, [isShared]);

    useEffect(() => {

        if (isDeletedComment) {
            getComments();
        }

    }, [isDeletedComment]);


    if (postId) {
        if (postInfo) {
            if (postOwner) {
                return (
                    <div className='w-75 h-auto m-auto d-grid gap-2'>
                        <div className='w-100 d-flex  align-items-center'>

                            <div className="card" style={{ width: '15%', height: 'auto' }}>
                                <img src={postOwner.ImageUrl} className="card-img-top" alt={postOwner._id} />
                                <div className="card-body">
                                    <h5 className="card-title">{postOwner.NickName}</h5>
                                </div>
                            </div>

                            <div className='d-grid gap-2' style={{ width: '85%' }}>
                                <div className='w-100'>
                                    <strong>Email: </strong>{user.Email}
                                </div>
                                <div className='w-100'>
                                    <strong>{language.includes("tr") ? "Kullanıcı Adım: " : "My Nick Name: "}</strong>{user.NickName}
                                </div>
                                {user.Name !== "" &&
                                    <div className='w-100'>
                                        <strong>{language.includes("tr") ? "Adım: " : "My Name: "}</strong>{user.Name}
                                    </div>
                                }
                                {user.Biography !== "" &&
                                    <div className='w-100'>
                                        <strong>{language.includes("tr") ? "Biyografi: " : "My Biography: "}</strong>{user.Biography}
                                    </div>}
                                <div className='w-100'>
                                    <strong>{language.includes("tr") ? "Yazılarım: " : "My Posts: "}</strong>{user.Post}
                                </div>
                            </div>
                        </div>
                        <ShareComment postId={postId} isShared={isShared} setIsShared={setIsShared} />
                        {comments.length !== 0 ?
                            <Comments comments={comments} setComments={setComments} isDeletedComment={isDeletedComment} setIsDeletedComment={setIsDeletedComment} />
                            :
                            <div class="alert alert-primary" role="alert">
                                {language.includes("tr") ? "Bu yazıya henüz yorum yapılmamış." : "There are no comments on this post yet."}
                            </div>
                        }
                    </div>
                )
            }
        }
    }
}

export default PostDetails
