import React, { useState } from 'react'
import { language } from '../../utils/utils'
import axios from 'axios'
import { toast } from 'react-hot-toast'
import { useNavigate } from 'react-router-dom';

function Login() {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    axios.post('http://localhost:3001/api/login', { email: email, password: password }).then(response => {
      if (response.data.user) {
        localStorage.setItem('userId',response.data.user._id);
        navigate('/home');
      } else {
        if (response.data.error) {
          toast.error(response.data.message)
        } else {
          console.log(response.data.user)
        }
      }
    }).catch(error => {
      toast.error(error)
    });
  }

  return (
    <form className='w-50 m-auto mt-5' onSubmit={handleSubmit}>
      <label className='form-label'>E-mail</label>
      <input value={email} onChange={e => setEmail(e.target.value)} type='email' className='w-100 form-control mb-3' placeholder='E-mail' />

      <label className='form-label'>Password</label>
      <input value={password} onChange={e => setPassword(e.target.value)} type='password' className='w-100 form-control mb-3' placeholder='Password' />

      <button type='submit' className='btn btn-primary w-100'>
        {language.includes("tr") ? "Giriş Yap" : "Login"}
      </button>
    </form>
  )
}

export default Login
