import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { mainUrl } from '../../utils/utils';
import axios from 'axios'

function PostDetails() {

    const { allPosts } = useSelector(state => state.allPosts);
    const params = useParams();
    const { postId } = params;
    const [postInfo, setPostInfo] = useState({});
    const [postOwner, setPostOwner] = useState({});

    const getPostInfo = async () => {
        const info = allPosts.filter(post => post._id === postId);
        setPostInfo(info[0]);
    }

    const getUser = async () => {
        axios.get(`${mainUrl}/api/getUser/${postInfo.OwnerId}`)
            .then((result) => {
                if (result.data.error) {
                    console.log(result.data.message)
                } else {
                    setPostOwner(result.data.user);
                }
            })
    }

    useEffect(() => {

        if (postId){
            getPostInfo();
        }

        return () => {
            setPostInfo({});
        }
    }, [postId]);

    useEffect(() => {

        if (postInfo){
            getUser();
        }

        return () => {
            setPostOwner({});
        }
    }, [postInfo]);


    if (postId) {
        if (postOwner) {
            return (
                <div className='w-75 h-auto m-auto d-flex'>
                    <div style={{ width: '10%', height: 'auto' }} className='d-grid gap-2 border rounded-start p-2'>
                        <img className='w-75 m-auto rounded-circle' src={postOwner.ImageUrl} />
                        <strong className='w-75 m-auto'>
                            {postOwner.NickName}
                        </strong>
                    </div>

                    <div style={{ width: '90%', height: 'auto' }} className='d-grid gap-2 border rounded-end p-2'>
                       
                    </div>
                </div>
            )
        }
    }
}

export default PostDetails
