import React from 'react'
import { language } from '../../utils/utils'
import axios from 'axios'
import {toast} from 'react-hot-toast'

function Login() {

  const handleSubmit = async (e) => {
    axios.put('http://localhost:3001/api/register', { email: email, password: password }).then(response => {
      if (response.data.error) {
        toast.error(response.data.message)
      } else {
        console.log(response.data.user)
      }
    }).catch(error => {
      toast.error(error)
    });
  }

  return (
    <form className='w-50 m-auto mt-5' onClick={handleSubmit()}>
      <label className='form-label'>E-mail</label>
      <input type='email' className='w-100 form-control mb-3' placeholder='E-mail' />
      <label className='form-label'>Password</label>
      <input type='password' className='w-100 form-control mb-3' placeholder='Password' />
      <button className='btn btn-primary w-100'>
        {language.includes("tr") ? "Giriş Yap" : "Login"}
      </button>
    </form>
  )
}

export default Login
