import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { subjectList } from '../../utils/utils';

function Home() {

  const { user } = useSelector(state => state.auth)
  const { allPosts } = useSelector(state => state.allPosts);
  const [allPostsList, setAllPostsList] = useState([]);
  const [allPageCount, setAllPageCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [currentPosts, setCurrentPosts] = useState([])

  useEffect(() => {

    setAllPostsList(allPosts);
    setAllPageCount(Math.ceil(allPosts.length / 10));

    return () => {
      setAllPostsList([]);
    }
  }, [allPosts])

  useEffect(() => {

    const indexOfFirst = (currentPage - 1) * 10;
    const indexOfLast = (currentPage + 9);

    console.log(indexOfFirst)
    console.log(indexOfLast)

    return () => {
      setCurrentPosts([]);
    }
  }, [currentPage])


  if (user) {
    if (allPostsList.length !== 0) {
      return (
        <div className='w-100'>
          {allPostsList.length}
          {allPageCount}
          <nav aria-label="Page navigation example">
            <ul class="pagination">
              <li class="page-item"><a class="page-link" href="#">Previous</a></li>
              {allPageCount > 3 ?
                <>
                  <li class="page-item"><a class="page-link" href="#">1</a></li>
                  <li class="page-item"><a class="page-link" href="#">2</a></li>
                  <li class="page-item"><a class="page-link" href="#">3</a></li>
                </>
                : null}
              <li class="page-item"><a class="page-link" href="#">Next</a></li>
            </ul>
          </nav>
        </div>
      )
    } else {
      return (
        <div className="w-100 d-grid gap-2 p-2" aria-hidden="true">
          {subjectList.map((subject, index) => {
            return (
              <div key={index} className="d-flex gap-2 card-text placeholder-glow border p-1 rounded" aria-hidden="true">
                <img src="https://t3.ftcdn.net/jpg/03/46/83/96/360_F_346839683_6nAPzbhpSkIpb8pmAwufkC7c5eD7wYws.jpg" className='placeholder' style={{ width: 48, height: 48 }} alt={index} />
                <span className='placeholder col-9'></span>
                <span className='placeholder col-2'></span>
                <a href="#" tabindex="-1" className="btn btn-danger disabled placeholder"></a>
              </div>

            )
          })}
        </div>
      )
    }
  } else {
    return (
      <div className="w-100 d-grid gap-2 p-2" aria-hidden="true">
        {subjectList.map((subject, index) => {
          return (
            <div key={index} className="d-flex gap-2 card-text placeholder-glow border p-1 rounded" aria-hidden="true">
              <img src="https://t3.ftcdn.net/jpg/03/46/83/96/360_F_346839683_6nAPzbhpSkIpb8pmAwufkC7c5eD7wYws.jpg" className='placeholder' style={{ width: 48, height: 48 }} alt={index} />
              <span className='placeholder col-9'></span>
              <span className='placeholder col-2'></span>
              <a href="#" tabindex="-1" className="btn btn-danger disabled placeholder"></a>
            </div>

          )
        })}
      </div>
    )
  }
}

export default Home
