import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { mainUrl } from '../../utils/utils'

function CommentInfo({ commentInfo }) {

    const [commentOwner, setCommentOwner] = useState({})

    const getCommentOwner = async () => {
        axios.get(`${mainUrl}/api/getUser/${commentInfo.OwnerId}`)
            .then((result) => {
                if (result.data.error) {
                    console.log(result.data.message)
                } else {
                    setCommentOwner(result.data.user);
                }
            })
    }

    useEffect(() => {

        getCommentOwner();

        return () => {
            setCommentOwner({});
        }
    }, [commentInfo])


    if (commentInfo) {
        if (commentOwner) {
            return (
                <div className='w-100 border d-flex'>
                    <div style={{ width: '10%', height: 'auto' }} className='border border-primary rounded-start p-2 justify-content-center'>
                        <img className='d-flex w-75 m-auto rounded-circle' src={commentOwner.ImageUrl} />
                    </div>

                    <div style={{ width: '90%', height: 'auto' }} className='d-grid gap-2 border border-dark rounded-end p-2'>
                        {commentInfo.Text}
                    </div>
                </div>
            )
        }
    }
}

export default CommentInfo
