import React, { useState } from 'react'
import { language } from '../../utils/utils'

function Register() {

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  return (
    <form className='w-50 m-auto mt-5'>
      <label className='form-label'>E-mail</label>
      <input value={email} onChange={e => setEmail(e.target.value)} type='email' className='w-100 form-control mb-3' placeholder='E-mail' />

      <label className='form-label'>Password</label>
      <input value={password} onChange={e => setPassword(e.target.value)} type='password' className='w-100 form-control mb-3' placeholder='Password' />

      <button className='btn btn-primary w-100'>
        {language.includes("tr") ? "Kayıt Ol" : "Register"}
      </button>
    </form>
  )
}

export default Register
