import React, { useState } from 'react'
import { useSelector } from 'react-redux'
import Formik from 'formik'
import yup from 'yup'
import FileBase64 from 'react-file-base64';
import axios from 'axios'
import { mainUrl } from '../../utils/utils'

function ProfileEdit() {

    const { user } = useSelector(state => state.auth)
    const [file, setFile] = useState(null);

    const handleChangeImage = async () => {
        console.log(file)
        try {
            const res = await axios.post(
                `${mainUrl}/api/updateUserImage`,
                { file: file },
            );
            console.log(res);
        } catch (ex) {
            console.log(ex);
        }
    }

    if (user) {
        return (
            <div className='w-50 m-auto'>
                <div class="mb-3 d-grid">
                    <img className='w-25 m-auto' src={user.ImageUrl} alt={user.id} />
                    <div className='d-flex gap-5'>
                        <FileBase64 multiple={false} onDone={({ base64 }) => setFile(base64)} />
                        <button disabled={!file} onClick={() => handleChangeImage()} className={`w-25 btn btn-${file ? "primary" : "secondary"}`}>Update Image</button>
                    </div>
                </div>
                <form className='w-100 d-grid gap-4'>
                    <div>
                        <label className='form-label'>Nick Name</label>
                        <input className='form-control' type='text' placeholder='Nick Name' />
                    </div>
                    <div>
                        <label className='form-label'>Name</label>
                        <input className='form-control' type='text' placeholder='Name' />
                    </div>
                    <div>
                        <label className='form-label'>Cinsiyet</label>
                        <input className='form-control' type='text' placeholder='||||||||||||' />
                    </div>
                    <div>
                        <label className='form-label'></label>
                        <input className='form-control' type='text' placeholder='||||||||||||' />
                    </div>
                </form>
            </div>
        )
    } else {
        return (
            <></>
        )
    }
}

export default ProfileEdit
