import React, { useEffect } from 'react'
import Navbar from './Components/Navbar'
import { Routes, Route } from 'react-router-dom'
import { Toaster } from 'react-hot-toast'
import axios from 'axios'

import Register from './Pages/Auth/Register'
import Login from './Pages/Auth/Login'
import ResetPassword from './Pages/Auth/ResetPassword'

import Home from './Pages/Home/Home'
import { getCurrentUser, getPosts, mainUrl, setCurrentUserPosts, setPosts, setUserData } from './utils/utils'
import Profile from './Pages/Profile/Profile'
import ProfileEdit from './Pages/Profile/ProfileEdit'
import MainPage from './Pages/MainPage'
import PublishPost from './Pages/PublishPost/PublishPost'
import { useSelector } from 'react-redux'

function App() {

  const { user } = useSelector(state => state.auth);

  useEffect(() => {

    const userId = localStorage.getItem("userId");
    if (userId) {
      getCurrentUser(userId);
    console.log(userId)
    }

  }, [])


  useEffect(() => {

    if (user) {
      getPosts(user.id);
      console.log(user.Post)
    }

  }, [user]);

  useEffect(() => {

    const userId = localStorage.getItem('userId');
    if (userId) {
      getCurrentUser(userId);
      getPosts(userId);
    }

  }, [localStorage.getItem('userId')]);

  return (
    <>
      <Toaster position='top-left' />
      <Navbar />
      <div className='w-100 mt-2 p-1'>
        <Routes>
          <Route path='/' element={<MainPage />} />
          <Route path='/home' element={<Home />} />
          <Route path='/register' element={<Register />} />
          <Route path='/login' element={<Login />} />
          <Route path='/resetpassword' element={<ResetPassword />} />
          <Route path='/profile' element={<Profile />} />
          <Route path='/profileEdit' element={<ProfileEdit />} />
          <Route path='/publishPost' element={<PublishPost />} />
        </Routes>
      </div>
    </>
  )
}

export default App
