import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { mainUrl } from '../../utils/utils'

function CommentInfo({ commentInfo }) {

    const [commentOwner, setCommentOwner] = useState({});

    const getCommentOwner = async () => {
        axios.get(`${mainUrl}/api/getUser/${commentInfo.OwnerId}`)
            .then((result) => {
                if (result.data.error) {
                    console.log(result.data.message)
                } else {
                    setCommentOwner(result.data.user);
                }
            });
    }

    useEffect(() => {

        getCommentOwner();

        return () => {
            setCommentOwner({});
        }
    }, [commentInfo]);


    if (commentInfo) {
        if (commentOwner) {
            return (
                <div className='w-100 d-flex'>
                    <div class="card" style={{ width: '15%', height: 'auto' }}>
                        <img src={commentOwner.ImageUrl} class="card-img-bottom" alt={commentOwner._id} />
                        <div class="card-body">
                            <h5 class="card-title">{commentOwner.NickName}</h5>
                        </div>
                    </div>

                    <div style={{ width: '85%', height: 'auto' }} className='d-grid gap-2 border rounded-end p-2'>
                        {commentInfo.Text}
                    </div>
                </div>
            )
        }
    }
}

export default CommentInfo
