import React, { useState } from 'react'
import { language, mainUrl, setUserData, setUserInfos } from '../../utils/utils'
import axios from 'axios'
import { toast } from 'react-hot-toast'
import { useNavigate } from 'react-router-dom';

function Login({ setUser }) {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    axios.post(`${mainUrl}/api/login`, { email: email, password: password }).then(response => {
      if (response.data.user) {
        localStorage.setItem('userData', JSON.stringify({
          id: response.data.user._id,
          Email: response.data.user.Email,
          NickName: response.data.user.NickName,
          Name: response.data.user.Name,
          Biography: response.data.user.Biography,
          Post: response.data.user.Post,
          ImageUrl: response.data.user.ImageUrl,
          EmailVerify: response.data.user.EmailVerify,
          Gender: response.data.user.Gender,
        }));
        setUserData(localStorage.getItem('userData'))
        navigate('/home', { replace: true });
      } else {
        if (response.data.error) {
          toast.error(response.data.message)
        } else {
        }
      }
    }).catch(error => {
      toast.error(error)
    });
  }

  return (
    <form className='w-50 m-auto mt-5' onSubmit={handleSubmit}>
      <label className='form-label'>E-mail</label>
      <input value={email} onChange={e => setEmail(e.target.value)} type='email' className='w-100 form-control mb-3' placeholder='E-mail' />

      <label className='form-label'>Password</label>
      <input value={password} onChange={e => setPassword(e.target.value)} type='password' className='w-100 form-control mb-3' placeholder='Password' />

      <button type='submit' className='btn btn-primary w-100'>
        {language.includes("tr") ? "Giriş Yap" : "Login"}
      </button>
    </form>
  )
}

export default Login
