import React from 'react'
import { language } from '../../utils/utils'

function ShareComment() {
  return (
    <div className='w-100 p-1 border m-auto d-flex gap-2'>
        <input className='form-control' placeholder={language.includes("tr")?"Ne Düşünüyorsun?":"What Do You Think?"} />
        <button className='btn btn-primary'></button>
    </div>
  )
}

export default ShareComment
