import React, { useState } from 'react'
import { useSelector } from 'react-redux'
import Formik from 'formik'
import yup from 'yup'
import axios from 'axios'
import { mainUrl } from '../../utils/utils'

function ProfileEdit() {

    const { user } = useSelector(state => state.auth)
    const [imageUrl, setImageUrl] = useState(null);

    const handleChangeImage = async () => {
        axios.post(`${mainUrl}/api/updateUserPhoto`, {
            file: imageUrl
        })
    }

    if (user) {
        return (
            <div className='w-50 m-auto'>
                <div class="mb-3 d-grid">
                    <img className='w-25 m-auto' src={user.ImageUrl} alt={user.id} />
                    <div className='d-flex gap-5'>
                        <input onChange={e => setImageUrl(e.currentTarget.files[0])} className="w-70 form-control form-control-sm" id="formFileSm" type="file" />
                        <button disabled={!imageUrl} onClick={() => handleChangeImage()} className={`w-25 btn btn-${imageUrl ? "primary" : "secondary"}`}>Update Image</button>
                    </div>
                </div>
                <form className='w-100 d-grid gap-4'>
                    <div>
                        <label className='form-label'>Nick Name</label>
                        <input className='form-control' type='text' placeholder='Nick Name' />
                    </div>
                    <div>
                        <label className='form-label'>Name</label>
                        <input className='form-control' type='text' />
                    </div>
                    <div>
                        <label className='form-label'>Cinsiyet</label>
                        <input className='form-control' type='text' />
                    </div>
                    <div>
                        <label className='form-label'></label>
                        <input className='form-control' type='text' />
                    </div>
                </form>
            </div>
        )
    } else {
        return (
            <></>
        )
    }
}

export default ProfileEdit
