import React, { useState } from 'react'
import { getCurrentUser, language, mainUrl, reportTitleList, subjectList } from '../../utils/utils'
import axios from 'axios'
import { toast } from 'react-hot-toast'
import { useFormik } from 'formik'
import { publishPostValidations } from '../../utils/validations'
import moment from 'moment'
import { useSelector } from 'react-redux'

function Report({ data }) {

    const { user } = useSelector(state => state.auth);
    const [isPublished, setIsPublished] = useState(false);
    const [files, setFiles] = useState([]);

    const { handleChange, handleSubmit, handleReset, values, errors, touched } = useFormik({
        initialValues: {
            title: '',
            detail: ''
        },
        onSubmit: values => {
            setIsPublished(true)
            let today = new Date();

            let todayy = today.getFullYear() + '' +
                ((today.getMonth() + 1) < 10 ? ("0" + (today.getMonth() + 1))
                    : (today.getMonth() + 1)) + '' + today.getDate() + ", " +
                today.getHours() + ":" +
                ((today.getMinutes()) < 10 ? ("0" + (today.getMinutes()))
                    : (today.getMinutes())) + ":" +
                today.getSeconds();

            const time = today.getFullYear() + '' +
                (today.getMonth() < 10 ? ("0" + today.getMonth()) : today.getMonth())
                + "" +
                (today.getDate() < 10 ? ("0" + today.getDate()) : today.getDate())
                + "" +
                (today.getHours() < 10 ? ("0" + today.getHours()) : today.getHours())
                + "" +
                (today.getMinutes() < 10 ? ("0" + today.getMinutes()) : today.getMinutes())
                + "" +
                (today.getSeconds() < 10 ? ("0" + today.getSeconds()) : today.getSeconds())
                + "" +
                (today.getMilliseconds() < 10 ? ("0" + today.getMilliseconds()) : today.getMilliseconds());

            axios.post(`${mainUrl}/api/publishPost`, {
                OwnerId: user.id,
                Subject: values.subject,
                Title: values.title,
                Text: values.text,
                Files: files,
                Comment: 0,
                FullDate: todayy,
                Date: moment().format('lll'),
                Time: time,
                Month: (today.getMonth() + 1),
            }).then((response) => {
                if (response.data.error) {
                    toast.error(response.data.message);
                    getCurrentUser(user.id)
                    setIsPublished(false);
                    handleReset();
                } else {
                    toast.success(response.data.message);
                    getCurrentUser(user.id);
                    setIsPublished(false);
                    handleReset();
                }
            });
        },
        validationSchema: publishPostValidations
    });

    return (
        <form className='w-75 m-auto mt-2 d-grid gap-2 mb-2' onSubmit={handleSubmit}>

            <div className='w-100 modal-body'>

                <div className='w-100'>
                    {language.includes("tr") ? "Konu" : "Subject"}
                    <select name='title' className='form-select' defaultValue={values.subject} onChange={handleChange}>
                        {reportTitleList.map(report => {
                            return (
                                <option key={report.value} selected={report.value === "1"}>
                                    {language.includes("tr") ? report.tr : report.tr}
                                </option>
                            )
                        })}
                    </select>
                </div>
{values.title}
                <div className='w-100'>
                    <label className='form-label'>
                        {language.includes("tr") ? "Detay" : "Detail"}
                    </label>
                    <div className="form-outline">
                        <textarea name='detail' style={{ minHeight: 200, maxHeight: 200 }} value={values.text} onChange={handleChange} className="form-control" rows="4" placeholder={language.includes("tr") ? "Detay" : "Detail"}></textarea>
                    </div>
                </div>
            </div>

            <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" onClick={() => { handleReset(); setFiles([]); }}>
                    {language.includes("tr") ? "Kapat" : "Close"}
                </button>
                {isPublished ?
                    <div className='btn btn-secondary'>
                        {language.includes("tr") ? "Yayınlanıyor..." : "Being Published..."}
                    </div>
                    :
                    <button type='submit' className='btn btn-primary'>
                        {language.includes("tr") ? "Yayınla" : "Publish"}
                    </button>}
            </div>
        </form>
    )
}

export default Report
