import React, { useEffect } from 'react'
import { getCurrentUser, language, mainUrl } from '../../../utils/utils'
import axios from 'axios'
import { toast } from 'react-hot-toast'
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik'
import { getEmailValidations } from '../../../utils/validations';

function GetEmail() {

  const navigate = useNavigate();

  const { handleChange, handleSubmit, handleReset, values, errors, touched } = useFormik({
    initialValues: {
      password: '',
      passwordConfirm: '',
    },
    onSubmit: values => {
      axios.post(`${mainUrl}/api/login`, { password: values.password, passwordConfirm: values.passwordConfirm }).then(response => {
        if (response.data.user) {
          localStorage.setItem('userId', response.data.user._id);
          getCurrentUser(response.data.user._id)
          navigate('/home', { replace: true });
        } else {
          if (response.data.error) {
            toast.error(response.data.message);
            handleReset();
          } else {
            toast.error(response.data.message)
          }
        }
      }).catch(error => {
        toast.error(error)
      });
    },
    validationSchema: getEmailValidations
  });

  useEffect(() => {

    if (localStorage.getItem('userId')) {
      navigate('/home', { replace: true });
    }

  }, []);

    return (
        <form className='w-50 m-auto mt-5' onSubmit={handleSubmit}>
          <label className='form-label'>E-mail</label>
          <input name='password' value={values.password} onChange={handleChange} type='password' className='w-100 form-control mb-3' placeholder='E-mail' />
          {errors.password && touched.password &&
            <div class="bg-danger text-light p-2 rounded">
              {errors.password}
            </div>}
    
          <label className='form-label'>passwordConfirm</label>
          <input name='passwordConfirm' value={values.passwordConfirm} onChange={handleChange} type='passwordConfirm' className='w-100 form-control mb-3' placeholder='passwordConfirm' />
    
          <button type='submit' className='btn btn-primary w-100'>
            {language.includes("tr") ? "Giriş Yap" : "Login"}
          </button>
        </form>
      )
}

export default GetEmail
