import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import Formik from 'formik'
import yup from 'yup'
import FileBase64 from 'react-file-base64';
import axios from 'axios'
import { language, mainUrl } from '../../utils/utils'
import { useNavigate } from 'react-router-dom';

function ProfileEdit() {

    const navigate = useNavigate();
    const { user } = useSelector(state => state.auth);
    const [file, setFile] = useState(null);
    const [isUpdateImage, setIsUpdateImage] = useState(false)

    const handleChangeImage = async () => {
        try {
            setIsUpdateImage(true)
            await axios.post(
                `${mainUrl}/api/updateUserImage`,
                { file: file, userId: user.id },
            ).then(() => {
                localStorage.setItem('userData', JSON.stringify({ ...user, ImageUrl: file }));
                navigate('/home', { replace: true })
                navigate('/profileEdit', { replace: true })
                setIsUpdateImage(false)
            });

        } catch (ex) {
            console.log(ex);
        }
    }

    if (user) {
        return (
            <div className='w-50 m-auto'>
                <div className="mb-3 d-grid gap-4">
                    <img className='w-50 m-auto' src={user.ImageUrl} alt={user.id} />
                    <div className='d-flex gap-5'>
                        <FileBase64 multiple={false} onDone={({ base64 }) => setFile(base64)} />
                        {isUpdateImage ?
                            <div className='w-25 btn btn-primary d-flex justify-center align-center gap-4'>
                                <div className="spinner-border text-light" role="status">
                                    <span className="visually-hidden">Loading...</span>
                                </div>
                                <div>Loading...</div>
                            </div>
                            :
                            <button disabled={!file} onClick={() => handleChangeImage()} className={`w-25 btn btn-${file ? "primary" : "secondary"}`}>Update Image</button>}
                    </div>
                </div>
                <form className='w-100 d-grid gap-4'>
                    <div>
                        <label className='form-label'>Nick Name</label>
                        <input className='form-control' type='text' placeholder='Nick Name' />
                    </div>
                    <div>
                        <label className='form-label'>Name</label>
                        <input className='form-control' type='text' placeholder='Name' />
                    </div>
                    <div>
                        <label className='form-label'>Cinsiyet</label>
                        <div className='d-flex gap-5'>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="Gender" id="Man" checked={user.Gender === "Man" ? true : false} />
                                <label className="form-check-label" htmlFor="Man">
                                    {language.includes("tr") ? "Erkek" : "Man"}
                                </label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="Gender" id="Woman" checked={user.Gender === "Woman" ? true : false} />
                                <label className="form-check-label" htmlFor="Woman">
                                    {language.includes("tr") ? "Kadın" : "Woman"}
                                </label>
                            </div>
                        </div>
                    </div>
                    <div>
                        <label className='form-label'></label>
                        <input className='form-control' type='text' placeholder='||||||||||||' />
                    </div>
                </form>
            </div>
        )
    } else {
        return (
            <></>
        )
    }
}

export default ProfileEdit
