import React, { useEffect } from 'react'
import Navbar from './Components/Navbar'
import { Routes, Route } from 'react-router-dom'
import { Toaster } from 'react-hot-toast'
import axios from 'axios'

import Register from './Pages/Auth/Register'
import Login from './Pages/Auth/Login'
import ResetPassword from './Pages/Auth/ResetPassword'

import Home from './Pages/Home/Home'
import { mainUrl, setCurrentUserPosts, setPosts, setUserData } from './utils/utils'
import Profile from './Pages/Profile/Profile'
import ProfileEdit from './Pages/Profile/ProfileEdit'
import MainPage from './Pages/MainPage'
import PublishPost from './Pages/PublishPost/PublishPost'
import { useSelector } from 'react-redux'

function App() {

  const { user } = useSelector(state => state.auth);

  const getPosts = async () => {
    axios.get(`${mainUrl}/api/getPosts`).then((result) => {
      if (result.data.error) {
        console.log(result.data.message)
      } else {
        const myPosts = result.data.posts.filter((post) => post.OwnerId === user.id);
        setCurrentUserPosts(myPosts)
        setPosts(result.data.posts);
      }
    });
  }

  useEffect(() => {

    if(user){
      getPosts();
      console.log(user.Post)
    }

  }, [user.Post]);

  useEffect(() => {

    const userData = JSON.parse(localStorage.getItem('userData'));
    if (userData) {
      setUserData(userData);
      getPosts();
    }

  }, [localStorage.getItem('userData')]);

  return (
    <>
      <Toaster position='top-left' />
      <Navbar />
      <div className='w-100 mt-2 p-1'>
        <Routes>
          <Route path='/' element={<MainPage />} />
          <Route path='/home' element={<Home />} />
          <Route path='/register' element={<Register />} />
          <Route path='/login' element={<Login />} />
          <Route path='/resetpassword' element={<ResetPassword />} />
          <Route path='/profile' element={<Profile />} />
          <Route path='/profileEdit' element={<ProfileEdit />} />
          <Route path='/publishPost' element={<PublishPost />} />
        </Routes>
      </div>
    </>
  )
}

export default App
