import React, { useState } from 'react'
import { language, mainUrl } from '../../utils/utils'
import axios from 'axios'
import { toast } from 'react-hot-toast'

function Register() {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [nickName, setNickName] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post(`${mainUrl}/api/register`, { email: email, password: password }).then(response => {
      if (response.data.error) {
        toast.error(response.data.message)
      } else {
        toast.success(response.data.message);
        setEmail('');
        setPassword('');
        setNickName('');
      }
    }).catch(error => {
      toast.error(error)
    });
  }

  return (
    <form className='w-50 m-auto mt-5 d-grid gap-3' onSubmit={handleSubmit}>

      <label className='form-label'>E-mail</label>
      <input value={email} onChange={e => setEmail(e.target.value)} type='email' className='w-100 form-control' placeholder='E-mail' />

      <label className='form-label'>
        {language.includes("tr") ? "Şifre" : "Password"}
      </label>
      <input value={password} onChange={e => setPassword(e.target.value)} type='password' className='w-100 form-control' placeholder={language.includes("tr") ? "Şifre" : "Password"} />

      <label className='form-label'>
        {language.includes("tr") ? "Kullanıcı Adı" : "Nick Name"}
      </label>
      <input value={nickName} onChange={e => setNickName(e.target.value)} type='text' className='w-100 form-control' placeholder={language.includes("tr") ? "Kullanıcı Adı" : "Nick Name"} />

      <button type='submit' className='btn btn-primary w-100'>
        {language.includes("tr") ? "Kayıt Ol" : "Register"}
      </button>
    </form>
  )
}

export default Register
