import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { mainUrl } from '../../utils/utils'

function Postnfo({ post }) {

    const [postOwner, setPostOwner] = useState({})

    const getUser = async () => {
        axios.post(`${mainUrl}/api/getUser`, { userId: post.OwnerId })
            .then((response) => {
                if (response.data.error) {
                    console.log(response.data.message)
                } else {
                    setPostOwner(response.data.user)
                }
            })
    }

    useEffect(() => {

        getUser();

        return () => {

        }
    }, [post.OwnerId])


    return (
        <div className='w-100 p-1 border border-secondary rounded'>
            <strong>
                {post.Title}
            </strong>
        </div>
    )
}

export default Postnfo
