import React, { useState } from 'react'
import { language, mainUrl } from '../../utils/utils'
import axios from 'axios'
import { toast } from 'react-hot-toast'
import { useFormik } from 'formik'
import { publishPostValidations } from '../../utils/validations'

function PublishPost() {

    const subjectList = [
        {
            value: '1', tr: 'Lütfen Bir Konu Seçiniz...', en: 'Please Select a Subject...'
        }, {
            value: 'Software', tr: 'Yazılım', en: 'Software'
        }, {
            value: 'Hardware', tr: 'Donanım', en: 'Hardware'
        }, {
            value: 'Games', tr: 'Oyunlar', en: 'Games'
        }, {
            value: 'Shopping', tr: 'Alışveriş', en: 'Shopping'
        }, {
            value: 'Cryptocurrency', tr: 'Kripto Para', en: 'Cryptocurrency'
        }, {
            value: 'CultureArtEducation', tr: 'Kültür-Sanat-Eğitim', en: 'Culture-Art-Education'
        }, {
            value: 'SportsHealth', tr: 'Spor-Sağlık', en: 'Sports-Health'
        }, {
            value: 'SocialMedia', tr: 'Sosyal Medya', en: 'Social Media'
        }, {
            value: 'Help', tr: 'Yardım', en: 'Help'
        }, {
            value: 'Examine', tr: 'İnceleme', en: 'Examine'
        }, {
            value: 'Recommendation', tr: 'Öneri', en: 'Recommendation'
        }, {
            value: 'Other', tr: 'Diğer', en: 'Other'
        },
    ];

    const { handleChange, handleSubmit, handleBlur, handleReset, values, errors, touched } = useFormik({
        initialValues: {
            subject: '',
            title: '',
            text: '',
        },
        onSubmit: values => {
            if (values.subject !== "") {
                alert(values.subject)
            }
        },
        validationSchema: publishPostValidations
    });

    if (subjectList.length !== 0) {
        return (
            <form className='w-50 m-auto mt-5 d-grid gap-2' onSubmit={handleSubmit}>
                <div className='d-grid gap-2'>
                    {language.includes("tr") ? "Konu" : "Subject"}
                    <select name='subject' className='form-select' value={values.subject} onChange={handleChange}>
                        <option value="1">
                            {language.includes("tr") ? "Lütfen Bir Konu Seçiniz..." : "Please Select a Subject"}
                        </option>
                        {subjectList.map((subject) => {
                            return(
                                <option value={subject.value}>
                                {language.includes("tr") ? subject.tr : subject.en}
                            </option>
                            )
                        })}
                    </select>
                </div>
                {errors.subject && touched.subject &&
                    <div class="bg-danger text-light p-2 rounded">
                        {errors.subject}
                    </div>}

                <div>
                    <label className='form-label'>
                        {language.includes("tr") ? "Başlık" : "Title"}
                    </label>
                    <input name='title' value={values.title} onChange={handleChange} type='text' className='w-100 form-control' placeholder={language.includes("tr") ? "Başlık" : "Title"} />
                </div>
                {errors.title && touched.title &&
                    <div class="bg-danger text-light p-2 rounded">
                        {errors.title}
                    </div>}

                <div>
                    <label className='form-label'>
                        {language.includes("tr") ? "Metin" : "Text"}
                    </label>
                    <div className="form-outline">
                        <textarea name='text' style={{ minHeight: 200, maxHeight: 200 }} value={values.text} onChange={handleChange} className="form-control" rows="4" placeholder={language.includes("tr") ? "Metin" : "Text"}></textarea>
                    </div>
                </div>
                {errors.text && touched.text &&
                    <div class="bg-danger text-light p-2 rounded">
                        {errors.text}
                    </div>}

                <button type='submit' className='btn btn-primary w-100'>
                    {language.includes("tr") ? "Kayıt Ol" : "Register"}
                </button>
            </form>
        )
    }
}

export default PublishPost
