import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom';
import { language, subjectList } from '../../utils/utils';
import Posts from './Posts';

function Home() {

  const { user } = useSelector(state => state.auth);
  const { allPosts } = useSelector(state => state.allPosts);
  const [allPostsList, setAllPostsList] = useState([]);
  const [allPageCount, setAllPageCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [currentPosts, setCurrentPosts] = useState([])

  const getPosts = async () => {
    const list = [];
    Object.assign(list, allPosts);
    list.sort((a, b) => { return (b.Time) - (a.Time) });
    setAllPostsList(list);
  }

  useEffect(() => {

    getPosts();

    return () => {
      setAllPostsList([]);
    }

  }, [allPosts]);

  useEffect(() => {

    if (allPostsList.length !== 0) {
      setAllPageCount(Math.ceil(allPostsList.length / 15));
      setCurrentPage(1);
    }

    return () => {
      setAllPageCount(0);
      setCurrentPage(0);
    }
  }, [allPostsList]);

  useEffect(() => {

    if (allPostsList.length !== 0) {
      const indexOfFirst = (currentPage - 1) * 15;
      const indexOfLast = (indexOfFirst + 15);
      setCurrentPosts(allPostsList.slice(indexOfFirst, indexOfLast));
    }

    return () => {
      setCurrentPosts([]);
    }
  }, [currentPage]);


  if (user) {
    if (allPostsList.length !== 0) {
      return (
        <div className='w-100 d-grid gap-2'>
          <Posts posts={currentPosts} />
          <nav aria-label="Page navigation example">
            <ul className="pagination">
              {currentPage === 1 ?
                null : <li className="page-item"><button onClick={() => setCurrentPage(currentPage - 1)} className='page-link'>Previous</button></li>}
              {allPageCount > 3 ?
                <>
                  <li className="page-item"><a className="page-link" href="#">1</a></li>
                  <li className="page-item"><a className="page-link" href="#">2</a></li>
                  <li className="page-item"><a className="page-link" href="#">3</a></li>
                </>
                : null}
              {currentPage === allPageCount ?
                null : <li className="page-item"><button onClick={() => setCurrentPage(currentPage + 1)} className='page-link'>Next</button></li>}
            </ul>
          </nav>

          {subjectList.map((subject, index) => {
            if (subject.value !== "1") {
              return (
                <Link to={`/search/${subject.value}`} key={index} className='w-100 border rounded p-4 shadow-sm bg-secondary hover:bg-primary text-light cursor-pointer text-decoration-none'>
                  {language.includes("tr") ? subject.tr : subject.en}
                </Link>
              )
            }
          })}

        </div>
      )
    } else {
      return (
        <div className="w-100 d-grid gap-2 p-2" aria-hidden="true">
          {subjectList.map((subject, index) => {
            return (
              <div key={index} className="d-flex gap-2 card-text placeholder-glow border p-1 rounded" aria-hidden="true">
                <img src="https://t3.ftcdn.net/jpg/03/46/83/96/360_F_346839683_6nAPzbhpSkIpb8pmAwufkC7c5eD7wYws.jpg" className='placeholder' style={{ width: '3%', maxHeight: 36 }} alt={index} />
                <span className='placeholder' style={{ width: '65%' }}></span>
                <span className='placeholder w-25'></span>
                <a href="#" tabIndex="-1" className="btn btn-danger disabled placeholder" style={{ width: '7%' }}></a>
              </div>

            )
          })}
        </div>
      )
    }
  } else {
    return (
      <div className="w-100 d-grid gap-2 p-2" aria-hidden="true">
        {subjectList.map((subject, index) => {
          return (
            <div key={index} className="d-flex gap-2 card-text placeholder-glow border p-1 rounded" aria-hidden="true">
              <img src="https://t3.ftcdn.net/jpg/03/46/83/96/360_F_346839683_6nAPzbhpSkIpb8pmAwufkC7c5eD7wYws.jpg" className='placeholder' style={{ width: '3%', maxHeight: 36 }} alt={index} />
              <span className='placeholder' style={{ width: '65%' }}></span>
              <span className='placeholder w-25'></span>
              <a href="#" tabIndex="-1" className="btn btn-danger disabled placeholder" style={{ width: '7%' }}></a>
            </div>

          )
        })}
      </div>
    )
  }
}

export default Home
