import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { logout } from '../store/auth';
import { language } from '../utils/utils';

function Navbar() {

  const dispatch = useDispatch();
  const { user } = useSelector(state => state.auth);
  const [userInfos, setUserInfos] = useState({})

  const SignOut = async () => {
    dispatch(logout());
    localStorage.removeItem('userData')
  }

  useEffect(() => {

    const userData = JSON.parse(localStorage.getItem('userData'));
    if (localStorage.getItem('userData')) {
      setUserInfos(userData);
    }

  }, [localStorage.getItem('userData')]);


  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <div className="container-fluid">
        <h1 className="navbar-brand">ForumOnline</h1>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon navbar-toggler-primary"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          {userInfos ?
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <NavLink className="nav-link" to={`/home`}>Home</NavLink>
              </li>

              <li className="nav-item dropdown">
                <NavLink className="nav-link dropdown-toggle text-secondary" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  <img className='rounded-circle me-2' width="24" height="24" src={userInfos.ImageUrl} />
                  {language.includes("tr") ? "Profil" : "Profile"}
                </NavLink>
                <ul className="dropdown-menu p-1 gap-1">
                  <li><NavLink className="dropdown-item rounded" to={`/profile`}>Profile</NavLink></li>
                  <li><NavLink className="dropdown-item rounded" to={`/profileEdit`}>ProfileEdit</NavLink></li>
                  <li><button className="dropdown-item rounded">E-mail Onayla</button></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><NavLink onClick={() => SignOut()} className="dropdown-item rounded" to={`/login`}>Sign Out</NavLink></li>
                </ul>
              </li>
            </ul>
            :
            <ul className="navbar-nav d-flex me-0" role="search">
              <li className="nav-item">
                <NavLink className="nav-link" to={`/login`}>Login</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link" to={`/register`}>Register</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link" to={`/resetpassword`}>Reset Password</NavLink>
              </li>
            </ul>}
        </div>
      </div>
    </nav>
  )
}

export default Navbar
